let serial;

function setup() {

  serial = new p5.SerialPort();
  serial.open("/dev/tty.usbmodem14201");
  serial.on('data', gotData);

  createCanvas(400, 400);
}

function draw() {
  background(220);
}

function gotData() {
  let inString = serial.readLine();
  println(inString);
}

html, body {
  margin: 0;
  padding: 0;
}
canvas {
  display: block;
}

let stars = [];
let lines = [];
let zassign = 0;

function setup() {
  createCanvas(windowWidth, windowHeight);

  for (let i = 0; i < width * 0.22; i++) {
    stars.push(new Constellation());
  }
}

function draw() {
  for (let i = 0; i < stars.length; i++) {
    stars[i].twinkle();
    stars[i].display();
  }

if (zassign == 1) {
  let size = 10;
  stroke(205);
  line(200, 400, 250, 350);
  ellipse(200, 400, size);
  ellipse(250, 350, size);
  line(210, 300, 250, 350);
  ellipse(210, 300, size);
  line(300, 270, 210, 300);
  ellipse(300, 270, size);
  line(340, 300, 300, 270);
  ellipse(340, 300, size);
  line(320, 330, 340, 300);
  ellipse(320, 330, size);
  line(315, 380, 320, 330);
  ellipse(315, 380, size);
  line(370, 330, 340, 300);
  ellipse(370, 330, size);
  line(390, 370, 370, 330);
  ellipse(390, 370, size);
}
}

function mousePressed() {
  zassign++;

  for (let i = 0; i < stars.length; i++) {
    stars[i].starClick(mouseX, mouseY);
   stars[i].lineDraw();
  }
}

html,
body {
  overflow: hidden;
  margin: 0;
  padding: 0;
  background: #000d23;
  /* For browsers that do not support gradients */
  background: -webkit-linear-gradient(#000d23, #0f343a);
  /* For Safari 5.1 to 6.0 */
  background: -o-linear-gradient(#000d23, #0f343a);
  /* For Opera 11.1 to 12.0 */
  background: -moz-linear-gradient(#000d23, #0f343a);
  /* For Firefox 3.6 to 15 */
  background: linear-gradient(#000d23, #0f343a);
  /* Standard syntax */
  position: relative;
}

.bg {
  position: absolute;
  width: 100%;
  height: 100%;
  bottom: 0;
  left: 0;
  background-size: 50%;
  background-repeat: repeat-x;
  background-position: bottom 0 left 0;
  z-index: 999;
}

@keyframes particleAnimation {
  28% {
    left: 0;
  }
  100% {
    left: 300%;
  }
}

.wrapper {
  position: absolute;
  width: 100%;
  height: 100%;
  top: 0;
  left: 0;
  transform: rotate(145deg);
  transform-origin: 88% 33%;
  z-index: 100;
}

.p {
  position: fixed;
  left: 0;
  top: 50px;
  width: 1px;
  height: 1px;
  background-color: white;
  animation-name: particleAnimation;
  animation-timing-function: linear;
  animation-iteration-count: infinite;
}

.p::before {
  position: absolute;
  display: block;
  content: "";
  width: 100px;
  right: 1px;
  top: 0;
  height: 1px;
  background: linear-gradient(to right, rgba(0, 0, 0, 0) 0%, rgba(255, 255, 255, 0.4) 100%);
}

.p-1 {
  animation-duration: 16s;
}

.p-2 {
  animation-duration: 6s;
  top: 45%;
}

.p-3 {
  animation-duration: 11s;
  top: 80%;
}

.p-4 {
  animation-duration: 9s;
  top: 66%;
}

.p-5 {
  animation-duration: 13s;
  top: 123%;
}

class Constellation {
  constructor() {
    this.x = random(width);
    this.y = random(height);
    this.d = random(2, 6.5);
    this.clicked = false;
  }

  display() {
    noStroke();
    if (this.clicked == true) {
      ellipse(this.x, this.y, this.d + 10);
    } else {
      ellipse(this.x, this.y, this.d);
    }
  }

  twinkle() {
    this.r = random(110, 255);
    this.g = random(207, 254);
    this.b = random(204, 255);
    fill(this.r, this.g, this.b);
  }

  starClick(mx, my) {
    if (dist(mx, my, this.x, this.y) < this.d / 2 + 3.8) {
      this.clicked = !this.clicked;
      lines.push(this.x, this.y);
    }
  }

  lineDraw() {
    if (lines.length == 4) {
      stroke(239, 255, 253, 95);
      line(lines[0], lines[1], lines[2], lines[3]);
      lines.splice(0, 2);
    }
  }
}

