  top: 50px;
  width: 1px;
  height: 1px;
  background-color: white;
  animation-name: particleAnimation;
  animation-timing-function: linear;
  animation-iteration-count: infinite;
}

.p::before {
  position: absolute;
  display: block;
  content: "";
  width: 100px;
  right: 1px;
  top: 0;
  height: 1px;
  background: linear-gradient(to right, rgba(0, 0, 0, 0) 0%, rgba(255, 255, 255, 0.4) 100%);
}

.p-1 {
  animation-duration: 16s;
}

.p-2 {
  animation-duration: 6s;
  top: 45%;
}

.p-3 {
  animation-duration: 11s;
  top: 80%;
}

.p-4 {
  animation-duration: 9s;
  top: 66%;
}

.p-5 {
  animation-duration: 13s;
  top: 123%;
}

class Constellation {
  constructor() {
    this.x = random(width);
    this.y = random(height);
    this.d = random(2, 6.5);
    this.clicked = false;
  }

  display() {
    noStroke();
    if (this.clicked == true) {
      ellipse(this.x, this.y, this.d + 10);
    } else {
      ellipse(this.x, this.y, this.d);
    }
  }

  twinkle() {
    this.r = random(110, 255);
    this.g = random(207, 254);
    this.b = random(204, 255);
    fill(this.r, this.g, this.b);
  }

  starClick(mx, my) {
    if (dist(mx, my, this.x, this.y) < this.d / 2 + 3.8) {
      this.clicked = !this.clicked;
      lines.push(this.x, this.y);
    }
  }

  lineDraw() {
    if (lines.length == 4) {
      stroke(239, 255, 253, 95);
      line(lines[0], lines[1], lines[2], lines[3]);
      lines.splice(0, 2);
    }
  }
}

let num = 0;
let pic;
let amount = 5;
let cellSize;
let regions = [];

function preload() {
  pic = loadImage("kahlo.jpg");
}

function setup() {
  createCanvas(pic.width, pic.height);
  let button = createButton("Snap");
  button.mousePressed(takePic);

  image(pic, 0, 0);

  cellSize = width / amount;

  for (let x = 0; x < width; x += cellSize) {
    for (let y = 0; y < height; y += cellSize) {
      // show the grid
      // noFill()
      // square(x, y, cellSize);
      let pixelInfo = pic.get(x, y, cellSize, cellSize);
      regions.push(new picRegion(pixelInfo));
    }
  }
}

function draw() {}

function mousePressed() {
  for (let x = 0; x < amount; x++) {
    for (let y = 0; y < amount; y++) {
      if (
        mouseX > x * cellSize &&
        mouseX < (x + 1) * cellSize &&
        mouseY > y * cellSize &&
        mouseY < (y + 1) * cellSize
      ) {
        let num = int(random(regions.length));
        image(regions[num].region, x * cellSize, y * cellSize);
      }
    }
  }
}

class picRegion {
  constructor(px) {
    this.region = px;
  }
}

function takePic() {
  num++;
  saveCanvas(`pic${num}.png`);
}

